mapper.insert:


SELECT MAX(product_idx) FROM product

INSERT 
	INTO product
	VALUES (
		#{product_idx} + 1
		,#{product_name}
		,#{product_brand}
		,#{product_size}
		,#{product_price}
		,#{product_release_price}
		,#{product_amount}
		,0
		,#{product_exp}
		,#{product_detail_exp}
		,#{product_color}
		,#{product_discount_price}
		,now()
		,0
	)



SELECT MAX(image_idx) FROM image

INSERT 
	INTO image (image_idx, product_idx, image_main_file, image_real_file1)
	VALUES (
		(#{image_idx} + 1)
		,#{product_idx}
		,#{image.image_main_file}
		,#{image.image_real_file1}

	)

mapper.update:
		UPDATE product
  			SET


 				product_name = #{product.product_name}

  			,product_brand = #{product_brand}
	        , product_price = #{product_price}
	        , product_discount_price = #{product_discount_price}
	        , product_release_price = #{product_release_price}
       		, product_size = #{product_size}
	        , product_amount = #{product_amount}
	        , product_color = #{product_color}
	        , product_exp = #{product_exp}
	        , product_detail_exp = #{product_detail_exp}
	    WHERE 
	        product_idx = #{product_idx}

		UPDATE image
  			SET

 				image_main_file = #{image_main_file}
 				, image_real_file1 = #{image_real_file1}

	    WHERE 
	        product_idx = #{product_idx}

mapper.select:
SELECT image_main_file, image_real_file1, image_real_file2  
	FROM image 
	WHERE product_idx = #{product_idx}

